import React, { useEffect, useState } from "react";
import "./App.css";
import Select from "react-select"
import ReactPlayer from "react-player";
import axios from "axios";

function App() {

  const [company, setCompany] = useState({
    id: -1,
    URL: "",
    Name: "",
    Socials: "",
    Contact: ""
  })
  const [videos, setVideos] = useState([])
  const [videoOptions, setVideoOptions] = useState([])
  const [videoCurrent, setVideoCurrent] = useState("")
  const [videoCurrentLink, setVideoCurrentLink] = useState("")

  useEffect(() => {
    getInfo();
  }, [])

  useEffect(() => {
    videos.forEach((video)=> {
      setVideoOptions([...videoOptions, {value: video.Name, label: video.Name}])
    })
  }, [videos])

  function getInfo() {
    axios
      .get(`http://localhost:5000/api/companies/energyti.me`)
      .then(res => {
          console.log("Company Data Response", res.data[0])
          setCompany(res.data)
          axios
            .get(`http://localhost:5000/api/videos/${res.data[0].id}`)
            .then(res => {

                setVideos(res.data)
            })
            .catch(err => {
                console.log(err)
            })
      })
      .catch(err => {
          console.log(err)
      })
  }

  function handleChange(event) {
    event.preventDefault()
    setVideoCurrent(event.target.value)
    setVideoCurrentLink(videos.find(video => video.Name == event.target.value).Link)
  }

  return (
    <div id="App">
      <div id="TopSection">
        <Select id="Videos"
            options={videoOptions}
            value={videoCurrent}
            onChange={ e => handleChange(e)}
        />
        {/* <h2 id="CompanySocials">{}</h2> */}
     </div>
     <div id="MiddleSection">
       <div id="VideoContainer">
        <ReactPlayer 
          url={videoCurrentLink}
          controls="true"
          playsinline="true"
        />
      </div>
     </div>
     <div id="BottomSection">
        <h1 id="CompanyName">{company.Name}</h1>
        <h3 id="CompanyEmail">{company.Email}</h3>
     </div>
    </div>
  );
}

export default App;
