import React, { useEffect, useState } from "react";
import "./App.css";
import ReactPlayer from "react-player";
import axios from "axios";

function App() {

  const [Name, setName] = useState("")
  const [Mantra, setMantra] = useState("")
  const [Video, setVideo] = useState("")
  const [Legal, setLegal] = useState("")
  const [Email, setEmail] = useState("")

  useEffect(() => {
    getInfo();
  }, [])

  function getInfo() {
    axios
      .get(`http://localhost:5000/api/companies/energyti.me`)
      .then(res => {
          console.log(res.data)
          axios
            .get(`http://localhost:5000/api/videos`, res.data.id)
            .then(res => {
                console.log(res.data)
            })
            .catch(err => {
                console.log(err)
            })
      })
      .catch(err => {
          console.log(err)
      })
  }

  return (
    <div id="App">
      <div id="TopSection">
        <h1 id="CompanyName">{Name}</h1>
        <h2 id="CompanyMatra">{Mantra}</h2>
     </div>
     <div id="MiddleSection">
       <div id="VideoContainer">
        <ReactPlayer 
          url={Video}
          controls="true"
          playsinline="true"
        />
      </div>
     </div>
     <div id="BottomSection">
        <h3 id="CompanyType">{Legal}</h3>
        <h3 id="CompanyEmail">{Email}</h3>
     </div>
    </div>
  );
}

export default App;
