import React, { useEffect, useState } from "react";
import "./App.css";
import Select from "react-select"
import ReactPlayer from "react-player";
import axios from "axios";

function App() {

  const [company, setCompany] = useState({
    id: -1,
    URL: "",
    Name: "",
    Socials: "",
    Contact: ""
  })
  const [videos, setVideos] = useState([])
  const [videoOptions, setVideoOptions] = useState([])
  const [videoCurrent, setVideoCurrent] = useState("")
  const [videoCurrentLink, setVideoCurrentLink] = useState("")

  useEffect(() => {
    getInfo();
  }, [])

  useEffect(() => {
    if (videoOptions.length == 0) {
      videos.forEach((video)=> {
        setVideoOptions([...videoOptions, {value: video.Name, label: video.Name}])
      })
    }
  }, [videos])

  function getInfo() {
    axios
      .get(`http://localhost:5000/api/companies/energyti.me`)
      .then(res => {

          setCompany(res.data[0])
          axios
            .get(`http://localhost:5000/api/videos/${res.data[0].id}`)
            .then(res => {

                setVideoCurrent(res.data[0].Name)
                setVideoCurrentLink(res.data[0].Link)
                setVideos(res.data)
            })
            .catch(err => {

            })
      })
      .catch(err => {

      })
  }

  function handleChange(data) {
    setVideoCurrent(data.value)
    setVideoCurrentLink(videos.find(video => video.Name == data.value).Link)
  }

  return (
    <div id="App">
      <div id="TopSection">
        <Select id="VideosInput"
            options={videoOptions}
            value={videoCurrent}
            inputValue={videoCurrent}
            onChange={ (data) => handleChange(data)}
            styles={
              border="none"
            }
        />
        {/* <h2 id="CompanySocials">{}</h2> */}
     </div>
     <div id="MiddleSection">
       <div id="VideoContainer">
        <ReactPlayer 
          url={videoCurrentLink}
          controls="true"
          playsinline="true"
        />
      </div>
     </div>
     <div id="BottomSection">
        <h1 id="CompanyName">{company.Name}</h1>
        <h3 id="CompanyEmail">{company.Contact}</h3>
     </div>
    </div>
  );
}

export default App;
